import React, { useState, useEffect } from 'react'
import { useParams } from 'react-router-dom';
import { Col,Row,Image,ListGroup } from 'react-bootstrap';
import axios from "axios"
import Button from 'react-bootstrap/Button';
import Form from 'react-bootstrap/Form';
import Placeholder from 'react-bootstrap/Placeholder';
import { Link } from 'react-router-dom';

function ProductDetails(props) {
    const [News, setNews] = useState({title: '', Detail: '', img: '', category:''});
    const [error, setError] = useState(null);
    const { id } = useParams();
    
    useEffect(() => {
      axios
        .get(`http://localhost:5000/news/${id}`)
        .then((response) => {
          setNews(response.data);
        })
        .catch((error) => {
          console.log(error);
          setError(error.message);
        });
    }, [id]);
  
    return (
        <>   <div>
          <Row>
<Col>
<Placeholder size='xs' xs={12}  style={{color:"green", marginTop:'50px'}} />
</Col>
<Col>
<h1 style={{color:"green",
          marginTop:'30px',
          marginLeft:'90px'
      }
      }>{News.title}</h1>
</Col>
<Col>
<Placeholder size='xs' xs={12}  style={{color:"green", marginTop:'50px'}} />
</Col>

          </Row>
      
        {error && <p>{error}</p>}
      </div>
      
      <Image style={{ marginTop:'50px',marginLeft:'350px'}}  src={'../'+News.img} alt={News.title} fluid />
    
       <p style={{ color:'grey',marginTop:'20px',marginLeft:'300px'}} >{News.category}</p>
       <p style={{ marginTop:'20px',marginLeft:'300px'}} >{News.dateofnews}</p> 
       <p style={{ marginTop:'20px',marginLeft:'300px'}} >{News.Detail}</p> 
       <Link  style={{ marginTop:'20px',marginLeft:'300px'}} >{News.website}</Link>
       
       <ListGroup style={{ marginTop:'20px',marginBottom:'100px',marginLeft:'300px',marginRight:'40'}} variant="flush">
      <ListGroup.Item> naqme<p> says:</p></ListGroup.Item>
      <ListGroup.Item>Dapibushhh ggg eee ttt fff  ac facilisis in</ListGroup.Item>
    </ListGroup>
    

<div style={{color: 'grey', border: 'solid',marginTop:'20px',marginLeft:'200px',marginRight:'30px'}}> 
       <Form style={{marginBottom:'20px' , marginTop:'20px',marginLeft:'20px',marginRight:'30px'}} action={`/sendcomment/${props.id}`} method="post" >
      

      <Form.Group className="mb-3" controlId="formBasicEmail">
        <Form.Label>Name</Form.Label>
        <Form.Control  type="text" name="Name"  />
      </Form.Group>

      <Form.Group className="mb-3">
        <Form.Label>Email</Form.Label>
        <Form.Control  type="email" name="Email" />
      </Form.Group>
      <Form.Group className="mb-3">
        <Form.Label>Comment</Form.Label>
        <Form.Control  type="text" name="comment"  />
      </Form.Group>
     
      <input type="hidden" name="newsItemId" value={props.id} />
     
      <Button variant="success" type="submit">
        Add
      </Button>
    
    </Form>
    </div>



      </>
      
      
    );
  }
export default ProductDetails
