import React, { useState, useEffect } from 'react'
import { useParams } from 'react-router-dom';
import { Col,Row,Image,ListGroup } from 'react-bootstrap';
import axios from "axios"
import Placeholder from 'react-bootstrap/Placeholder';
import { Link } from 'react-router-dom';
function ProductDetails(props) {
    const [News, setNews] = useState({title: '', Detail: '', img: '', category:''});
    const [error, setError] = useState(null);
    const { id } = useParams();
    const [name, setName] = useState('');
    const [email, setEmail] = useState('');
    const [comment, setComment] = useState('');
    function handleSubmit(event) {
      event.preventDefault();
      // Send form data to server here
    }
    useEffect(() => {
      axios
        .get(`http://localhost:5000/news/${id}`)
        .then((response) => {
          setNews(response.data);
        })
        .catch((error) => {
          console.log(error);
          setError(error.message);
        });
    }, [id]);
  
    return (
        <>   <div>
          <Row>
<Col>
<Placeholder size='xs' xs={12}  style={{color:"green", marginTop:'50px'}} />
</Col>
<Col>
<h1 style={{color:"green",
          marginTop:'30px',
          marginLeft:'90px'
      }
      }>{News.title}</h1>
</Col>
<Col>
<Placeholder size='xs' xs={12}  style={{color:"green", marginTop:'50px'}} />
</Col>

          </Row>
      
        {error && <p>{error}</p>}
      </div>
      
      <Image style={{ marginTop:'50px',marginLeft:'350px'}}  src={'../'+News.img} alt={News.title} fluid />
    
       <p style={{ color:'grey',marginTop:'20px',marginLeft:'300px'}} >{News.category}</p>
       <p style={{ marginTop:'20px',marginLeft:'300px'}} >{News.dateofnews}</p> 
       <p style={{ marginTop:'20px',marginLeft:'300px'}} >{News.Detail}</p> 
       <Link  style={{ marginTop:'20px',marginLeft:'300px'}} >{News.website}</Link>
      
    


       <div className="container bootstrap snippets bootdey">
      <div className="row">
        <div className="col-md-12">
          <div className="blog-comment">
            <h3 className="text-success">Comments</h3>
            <hr />
            <ul className="comments">
              {props.comments.map((item, index) => (
                <div className="post-comments" key={index}>
                  <p className="meta">
                    <a href="#">{item.Name}</a> says: <i className="pull-right" />
                  </p>
                  <p>{item.comment}</p>
                </div>
              ))}
            </ul>
          </div>
        </div>
      </div>
    </div>






       <form action={`/sendcomment/${props.id}`} method="post" onSubmit={handleSubmit}>
      <div className="w3-section">
        <label><i className="fa fa-user-o" aria-hidden="true"></i>  Nom </label>
        <input className="w3-input w3-border" type="text" name="Name" value={name} onChange={e => setName(e.target.value)} required />
      </div>
      <div className="w3-section">
        <label><i className="fa fa-envelope-o"></i>  Email</label>
        <input className="w3-input w3-border" type="text" name="Email" value={email} onChange={e => setEmail(e.target.value)} required />
      </div>
      <div className="w3-section">
        <label>Commentaire</label>
        <input className="w3-input w3-border" type="text" name="comment" value={comment} onChange={e => setComment(e.target.value)} required />
      </div>
      <input type="hidden" name="newsItemId" value={props.id} />
      <button type="submit" className="w3-button  w3-green w3-margin-bottom"><i className="fa fa-paper-plane-o" aria-hidden="true"></i>  Publier</button>
    </form>

      </>
      
      
    );
  }
export default ProductDetails
