import Container from 'react-bootstrap/Container';
import Row from 'react-bootstrap/Row';
import Col from 'react-bootstrap/Col';
import Button from 'react-bootstrap/Button';
import Form from 'react-bootstrap/Form';
import React, { useRef } from "react";



const Login = () => {

  const username=useRef()
  const password=useRef()
  const getusername=localStorage.getItem("username")
  const getPassword=localStorage.getItem("passwordData")
  const handleSubmit=()=>{
      if(username.current.value=="souhaila"&&password.current.value=="12345"){
          localStorage.setItem("username","souhaila")
          localStorage.setItem("passwordData","12345")
      }
  }

  return (
    <>
   
    <Container>
      <Row>
        <Col> 
        
        <img
              src="./images/imag2.jpg"
              
              className="d-inline-block align-top"
              alt="React Bootstrap logo"
            />
        </Col>
        <Col>
        <div className='mt-5'>
        <Form className="mb-6" onSubmit={handleSubmit}>
      <Form.Group className="mb-5" controlId="formBasicEmail">
        <Form.Label size="lg" className="mb-3">UserName</Form.Label>
        <Form.Control type="text" ref={username}  />
       
      </Form.Group>

      <Form.Group className="mb-4" controlId="formBasicPassword">
        <Form.Label>Password</Form.Label>
        <Form.Control size="lg" type="password"  ref={password} placeholder="Password" />
        <Form.Text className="text-muted">
          We'll never share your Password with anyone else.
        </Form.Text>
      </Form.Group>
     
      <Button variant="primary" type="submit">
        Submit
      </Button>
    </Form>
    </div>
        </Col>
      </Row>
    
    </Container>
    
    </>
  )
}

export default Login
