import React, { useState, useEffect } from 'react'
import { useParams } from 'react-router-dom';
import { Col,Row,Image,ListGroup } from 'react-bootstrap';
import axios from "axios"
import Placeholder from 'react-bootstrap/Placeholder';
function ProductDetails() {
    const [News, setNews] = useState({title: '', Detail: '', img: '', category:''});
    const [error, setError] = useState(null);
    const { id } = useParams();
  
    useEffect(() => {
      axios
        .get(`http://localhost:5000/news/${id}`)
        .then((response) => {
          setNews(response.data);
        })
        .catch((error) => {
          console.log(error);
          setError(error.message);
        });
    }, [id]);
  
    return (
        <>   <div>
          <Row>
<Col>
<Placeholder size='xs' xs={12}  style={{color:"green", marginTop:'50px'}} />
</Col>
<Col>
<h1 style={{color:"green",
          marginTop:'30px',
          marginLeft:'30px'
      }
      }>{News.title}</h1>
</Col>
<Col>
<Placeholder size='xs' xs={12}  style={{color:"green", marginTop:'50px'}} />
</Col>

          </Row>
      
        
        <p>{News.Detail}</p>
        {error && <p>{error}</p>}
      </div>
      
      <Col><Image src={'../'+News.img} alt={News.title} fluid /></Col>
        <Col> 
        <ListGroup variant="flush">
                <ListGroup.Item>{News.title}</ListGroup.Item>
                <ListGroup.Item>{News.Detail}</ListGroup.Item>
                <ListGroup.Item>{News.category}</ListGroup.Item>
               
             
       </ListGroup></Col>
      
      
      
      </>
      
      
    );
  }
export default ProductDetails
